mapper.insert:
UPDATE tb_login
SET passwd = #{passwd}
WHERE user_no = #{user_no}

mapper.select:
		SELECT count(*) FROM tb_user u
		INNER JOIN tb_login l
		ON u.user_no = l.user_no
		WHERE user_id= #{user_id}
        AND passwd = #{passwd}

	SELECT user_id FROM tb_user
	WHERE manager_nm = #{manager_nm}
	 
        AND manager_tel = #{manager_tel}
    
    
        AND manager_email = #{manager_email}

	SELECT u.user_no, l.passwd, u.manager_email FROM tb_user u
	INNER JOIN tb_login l
	ON u.user_no = l.user_no
	WHERE user_id= #{user_id}
	AND manager_nm = #{manager_nm}
	
        AND manager_tel = #{manager_tel}
    
    
        AND manager_email = #{manager_email}

SELECT a.user_no, a.user_id, a.company_nm, a.biz_reg_number, a.ceo_nm, CONCAT('(', a.post_code, ')', a.biz_addr_1, a.biz_addr_2) AS biz_addr_1, a.contact_tel, a.manager_nm, a.manager_tel, a.manager_email, DATE_FORMAT(a.reg_dt,'%Y.%m.%d') AS reg_dt FROM tb_user a INNER JOIN tb_login b ON a.user_no = b.user_no WHERE 1=1
AND b.aprv_status = #{aprv_status}
ORDER BY reg_dt desc

mapper.update:
UPDATE tb_login SET aprv_status = 'A' 
WHERE user_no IN 

      #{item.user_no}
